import React, { Component } from 'react';
import './anasayfa.css';
import Alertform from './alertform';
import AlertLinks from './alertLinks';
import { ButtonToolbar, ToggleButton, ToggleButtonGroup } from 'react-bootstrap';
import StarfieldAnimation from 'react-starfield-animation';



class AnaSayfa extends Component { 
    constructor(props){
        super(props);
        this.toggle_login = this.toggle_login.bind(this);
        this.toggle_signup = this.toggle_signup.bind(this);
        this.state = {
            isOpen: false
        };
    }
    toggle_login() {
        this.setState({
            isOpen: false
        });
    }
    toggle_signup() {
        this.setState({
            isOpen: true
        });
    }
    render(){
        let login = (
            <div className="login_signup">
              <Alertform></Alertform>
            </div>
        )
        let signup = (
            <div className = "login_signup">
              <AlertLinks></AlertLinks>
            </div>
        )
        let whichone = (this.state.isOpen ? signup : login );
        return (   
        <div   style={{
            background: `url(${background})`,
            backgroundSize: 'stretch',
            minHeight: '100vh',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center'
          }}> 
               
              <ButtonToolbar>
    <ToggleButtonGroup type="radio" name="options" defaultValue={1}>
      <ToggleButton value={1}  onClick = {this.toggle_login}>Radio 1 (pre-checked)</ToggleButton>
      <ToggleButton value={2} onClick = {this.toggle_signup}>Radio 2</ToggleButton>
    </ToggleButtonGroup>
  </ButtonToolbar>
  
            

          
            <div>{whichone}  </div>
            
        <StarfieldAnimation
          numParticles={400}
          style={{
            position: 'absolute',
            zIndex: 1,
            top: 0,
            left: 0,
            right: 0,
            bottom: 0
          }}
          
        />       
        </div>           
        );
    }  
}

export default AnaSayfa;
