import React, { Component } from 'react';
import './anasayfa.css';
import Alertform from './alertform';
import AlertLinks from './alertLinks';
import { Button, ButtonGroup, ButtonToolbar, ToggleButton, ToggleButtonGroup } from 'react-bootstrap';



class AnaSayfa extends Component { 
    constructor(props){
        super(props);
        this.toggle_login = this.toggle_login.bind(this);
        this.toggle_signup = this.toggle_signup.bind(this);
        this.state = {
            isOpen: false
        };
    }
    toggle_login() {
        this.setState({
            isOpen: false
        });
    }
    toggle_signup() {
        this.setState({
            isOpen: true
        });
    }
    render(){
        let login = (
            <div className="login_signup">
              <Alertform></Alertform>
            </div>
        )
        let signup = (
            <div className = "login_signup">
              <AlertLinks></AlertLinks>
            </div>
        )
        let whichone = (this.state.isOpen ? signup : login );
        return (   
        <div> 
               
               <>
  <Navbar bg="dark" variant="dark">
    <Navbar.Brand href="#home">Navbar</Navbar.Brand>
    <Nav className="mr-auto">
      <Nav.Link href="#home">Home</Nav.Link>
      <Nav.Link href="#features">Features</Nav.Link>
      <Nav.Link href="#pricing">Pricing</Nav.Link>
    </Nav>
    <Form inline>
      <FormControl type="text" placeholder="Search" className="mr-sm-2" />
      <Button variant="outline-info">Search</Button>
    </Form>
  </Navbar>
  <br />
  <Navbar bg="primary" variant="dark">
    <Navbar.Brand href="#home">Navbar</Navbar.Brand>
    <Nav className="mr-auto">
      <Nav.Link href="#home">Home</Nav.Link>
      <Nav.Link href="#features">Features</Nav.Link>
      <Nav.Link href="#pricing">Pricing</Nav.Link>
    </Nav>
    <Form inline>
      <FormControl type="text" placeholder="Search" className="mr-sm-2" />
      <Button variant="outline-light">Search</Button>
    </Form>
  </Navbar>

  <br />
  <Navbar bg="light" variant="light">
    <Navbar.Brand href="#home">Navbar</Navbar.Brand>
    <Nav className="mr-auto">
      <Nav.Link href="#home">Home</Nav.Link>
      <Nav.Link href="#features">Features</Nav.Link>
      <Nav.Link href="#pricing">Pricing</Nav.Link>
    </Nav>
    <Form inline>
      <FormControl type="text" placeholder="Search" className="mr-sm-2" />
      <Button variant="outline-primary">Search</Button>
    </Form>
  </Navbar>
</>
  
            

          
            <div>{whichone}  </div>
            
        </div>           
        );
    }  
}

export default AnaSayfa;
