import React, { Component } from 'react';
import background from './stars.jpg'
import AnaSayfa from './AnaSayfa';
import StarfieldAnimation from 'react-starfield-animation';
import ribbon from './ribbon.png';

class Karsilama extends Component {
    render() {
        return (
            <div style={{
                background: `url(${background})`,
                backgroundSize: 'stretch',
                minHeight: '100vh',
                justifyContent: 'center',
                alignItems: 'center'
              }}>

<div > 
              <button className= 'button' onClick = {this.toggle_signup}>Signup </button>
              
            

            </div>   <a href='https://github.com/transitive-bullshit/react-starfield-animation'>
          <img
            src={ribbon}
            alt='Fork me on GitHub'
            style={{
              position: 'center',
              zIndex: 100,
              top: 0,
              right: 0
            }}
          />
        </a>

        <h1
          style={{
            color: '#fff',
            fontSize: '3em',
            fontFamily: 'Quicksand, "Helvetica Neue", sans-serif',
            textShadow: '2px 2px 8px rgba(0, 0, 0, 0.5)'
          }}
        >
Alert System </h1>

        <StarfieldAnimation
          numParticles={400}
          style={{
            position: 'absolute',
            zIndex: 1,
            top: 0,
            left: 0,
            right: 0,
            bottom: 0
          }}
        />


</div>
        )
    }
}
export default Karsilama;
